import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios"; // Import Axios library
import './Configuration.css';
import Modal from '@material-ui/core/Modal';

function Configuration({ userId }) {
  const [open, setOpen] = React.useState(false);
  const [buildingType, setBuildingType] = useState("");
  const [buildingCost, setBuildingCost] = useState("");
  const [constructionTime, setConstructionTime] = useState("");

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleCreateConfiguration = () => {
    const data = {
      UserId: userId,
      BuildingType: buildingType,
      BuildingCost: parseFloat(buildingCost),
      ConstructionTime: parseFloat(constructionTime),
    };

    // Make a POST request to create a configuration
    axios.post("https://localhost:44388/api/Auth/CreateConfiguration", data)
      .then((response) => {
        // Configuration created successfully
        alert("Configuration created successfully");
        handleClose();
      })
      .catch((error) => {
        // Handle API call errors here
        alert("Error creating configuration: " + error.message);
      });
  };

  if (!userId) {
    return <Navigate to="/login" />;
  }

  return (
    <div>
      <h2>Configuration Page</h2>
      <table>
        <thead>
          <tr>
            <th>#</th>
            <th>Building Type</th>
            <th>Building Cost</th>
            <th>Construction Time</th>
          </tr>
        </thead>
      </table>
      <div style={{ display: 'block', padding: 30 }}>
        <button type="button" onClick={handleOpen}>
          Add Building
        </button>
        <Modal
          onClose={handleClose}
          open={open}
          style={{
            position: 'absolute',
            border: '2px solid #000',
            backgroundColor: 'gray',
            boxShadow: '2px solid black',
            height: 240,
            width: 720,
            margin: 'auto'
          }}
        >
          <div style={{ padding: '16px' }}>
            <div className="row">
              <label>Building Type</label>
              <input type="text" onChange={(e) => setBuildingType(e.target.value)} />
            </div>
            <div className="row">
              <label>Building Cost</label>
              <input type="text" onChange={(e) => setBuildingCost(e.target.value)} />
            </div>
            <div className="row">
              <label>Construction Time</label>
              <input type="text" onChange={(e) => setConstructionTime(e.target.value)} />
            </div>
            <div className="row">
              <button onClick={handleCreateConfiguration}>Create Configuration</button>
            </div>
          </div>
        </Modal>
      </div>
    </div>
  );
}

export default Configuration;
