import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import axios from "axios";
import "./App.css";
import Home from "./Home";
import Navbar from "./Navbar";
import Registration from "./Registration";
import Login from "./Login";
import Configuration from "./Configuration";

function App() {
  const [user, setUser] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    // Check the user token with the server
    const token = localStorage.getItem('token');
    if (token) {
      // Send a request to the server to validate the token
      const validateUrl = 'https://localhost:44388/api/Auth/ValidateToken';

      axios.post(validateUrl, { token })
        .then((response) => {
          const userData = response.data.user.username;
          const userId_ = response.data.user.id;

          setUser(userData);
          setUserId(userId_);
        })
        .catch((error) => {
          handleLogout();
        });
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setUserId(null);
  };

  return (
    <div className="App">
      <Router>
        <Navbar user={user} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Navigate to="/home" />} />
          <Route exact path="/" render={() => <Navigate to="/home" />} />
          <Route path="/home" element={<Home user={user} userId={userId} />} />
          <Route path="/login" element={<Login setUser={setUser} setUserId={setUserId} />} />
          <Route path="/configuration" element={<Configuration userId={userId} />} />
          <Route path="/registration" element={<Registration />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
