import React, { Fragment, useState } from "react";
import axios from "axios";
import { useNavigate, Navigate } from "react-router-dom";

function Registration(){
    const [username, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const history = useNavigate();

    const handleUsernameChange = (value) => {
        setUsername(value);
    }
    const handleEmailChange = (value) => {
        setEmail(value);
    }
    const handlePasswordChange = (value) => {
        setPassword(value);
    }

    const handleSave = () => {
        const data = {
            Username : username,
            Email : email,
            Password : password
        }

        console.log(data)

        const url = 'https://localhost:44388/api/Auth/Registration';

        axios.post(url, data)
        .then((result) => {
          alert('Registration successful');
          history('/login');
        })
        .catch((error) => {
          if (error.response && error.response.data) {
            alert('Registration failed: ' + error.response.data.data);
          } else {
            alert('Registration failed: An error occurred.');
          }
        });
    }

    

    return(
        <Fragment>
            <div>Registration</div>

            <label>Username</label>
            <input type="text" id="txtUsername" placeholder="Enter Username" onChange={(e) => handleUsernameChange(e.target.value)} /><br></br>
            
            <label>Email</label>
            <input type="email" id="Email" placeholder="Enter Email" onChange={(e) => handleEmailChange(e.target.value)} /><br></br>
            
            <label>Password</label>
            <input type="password" id="Password" placeholder="Enter Password" onChange={(e) => handlePasswordChange(e.target.value)} /><br></br>

            <button onClick={() => handleSave()}>Save</button>
        </Fragment>

    )

}

export default Registration
