import React, { Fragment, useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import "./Login.css"; // Import the CSS file

function Login({ setUser, setUserId }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const history = useNavigate();

  const handleUsernameChange = (value) => {
    setUsername(value);
  }

  const handlePasswordChange = (value) => {
    setPassword(value);
  }

  const handleLogin = () => {
    if (!username.trim() || !password.trim()) {
        alert("Please fill in all fields.");
        return;
    }

    const data = {
      Username: username,
      Password: password
    }
  
    const url = 'https://localhost:44388/api/Auth/Login';
  
    axios.post(url, data)
      .then((response) => {
        const token = response.data.token;
        // Store the token in localStorage
        localStorage.setItem('token', token);
        // Call a function to validate the token and set the user
        validateTokenAndSetUser(token);
      })
      .catch((error) => {
        if (error.response && error.response.data) {
          alert("Login failed: " + error.response.data.data);
        } else {
          alert("Login failed: An error occurred.");
        }
      });
  }
  

  const validateTokenAndSetUser = (token) => {
    const validateUrl = 'https://localhost:44388/api/Auth/ValidateToken';
  
    axios.post(validateUrl, { token })
      .then((response) => {
        const userData = response.data.user.username;
        const userId_ = response.data.user.id
        // Set the user data
        setUser(userData);
        setUserId(userId_);
        // Redirect to the home page
        history('/home');
      })
      .catch((error) => {
        // Handle token validation errors here (e.g., expired token)
        // You can redirect to the login page or perform any other action here
      });
  }

  return (
    <Fragment>
      <div className="login-container">
        <h2>Login</h2>
        <div className="form-group">
          <label htmlFor="txtUsername">Username</label>
          <input type="text" id="txtUsername" placeholder="Enter Username" onChange={(e) => handleUsernameChange(e.target.value)} />
        </div>
        <div className="form-group">
          <label htmlFor="Password">Password</label>
          <input type="password" id="Password" placeholder="Enter Password" onChange={(e) => handlePasswordChange(e.target.value)} />
        </div>
        <button className="login-btn" onClick={() => handleLogin()}>Login</button>
        <p>Don't have an account yet? <Link to="/registration">Create one!</Link></p>
      </div>
    </Fragment>
  );
}

export default Login;
