import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import axios from "axios";
import "./App.css";
import Home from "./Home";
import Navbar from "./Navbar";
import Registration from "./Registration";
import Login from "./Login";
import Configuration from "./Configuration";
import { validateTokenAndSetUser } from "./authUtils";

function App() {
  const [user, setUser] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    // Check for the token in local storage when the app loads
    const token = localStorage.getItem('token');
    if (token) {
      validateTokenAndSetUser(token, setUser, setUserId, navigate); // Use the utility function
    }
  }, []);

  const handleLogout = () => {
    // Remove the token, user, and userId from local storage
    localStorage.removeItem('token');
    setUser(null);
    setUserId(null);
  };

  return (
    <div className="App">
      <Router>
        <Navbar user={user} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Navigate to="/home" />} />
          <Route exact path="/" render={() => <Navigate to="/home" />} />
          <Route path="/home" element={<Home user={user} userId={userId} />} />
          <Route path="/login" element={<Login setUser={setUser} setUserId={setUserId} />} />
          <Route path="/configuration" element={<Configuration userId={userId} />} />
          <Route path="/registration" element={<Registration />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
