import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import axios from "axios";
import "./App.css";
import Home from "./Home";
import Navbar from "./Navbar";
import Registration from "./Registration";
import Login from "./Login";
import Configuration from "./Configuration";

function App() {
  const [user, setUser] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    // Check for the token in localStorage

    const token = localStorage.getItem('token');

    if (token) {
      // Validate the token with the backend and retrieve user information
      axios.post('https://localhost:44388/api/Auth/ValidateToken', { token })
        .then((response) => {
          const userData = response.data.user;
          console.log(response.data.Id)
          setUser(userData);
        })
        .catch((error) => {
          // Handle token validation errors (e.g., expired token)
          // You can redirect to the login page or perform any other action here
        });
    } 

  }, []);

  const handleLogout = () => {
    // Remove the token from localStorage
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <div className="App">
      <Router>
        <Navbar user={user} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Navigate to="/home" />} />
          <Route exact path="/" render={() => <Navigate to="/home" />} />
          <Route path="/home" element={<Home user={user} />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/configuration" element={<Configuration user={user} />} />
          <Route path="/registration" element={<Registration />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
